import '../App.css';
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';
import  { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function AjouterContact(){

    const [contact, setContact] = useState([]); 
    const [phone, setPhone] = useState("");
    const [nom, setNom] = useState("");
    const [mail, setMail] = useState("");
    const [tel, setTel] = useState("");

    const handleClick=()=>{
        axios.post("http://localhost:8000/api/post",{nom,mail,tel}).then(()=>{
            setNom("")
            setMail("")
            setTel("")
        })
    }

    const pageStyle = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        backgroundColor: '#f0f2f5',
      };

      const formContainerStyle = {
        background: 'white',
        padding: '2rem',
        borderRadius: '8px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        width: '100%',
        maxWidth: '400px',
      };

      const inputStyle = {
        width: '100%',
        padding: '10px',
        margin: '10px 0',
        borderRadius: '4px',
        border: '1px solid #ddd',
        boxSizing: 'border-box',
      }; 
      
      const labelStyle = {
        display: 'block',
        marginBottom: '5px',
        fontWeight: 'bold',
        textAlign: 'left',  
        width: '100%',
      };

      const buttonStyle = {
        width: '100%',
        padding: '10px',
        backgroundColor: '#619caf',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        fontSize: '16px',
        marginTop: '20px',
      };

      const phoneInputStyle = {
        ...inputStyle,
        paddingLeft: '50px', // Espace pour le drapeau et le préfixe
      };
    
      const handlePhoneChange = (value, country) => {
        setPhone(value);
      };
    
      const handleChange = (value) => {
        setContact(value)
      };
    

    return(
        <>
<div className="App" style={pageStyle}>
      <div style={formContainerStyle}>
        <form 
        // onSubmit={handleSubmit} 
        >
          <div>
            <label style={labelStyle}>Nom :</label>
            <input
            type="text"
            placeholder="Entrez le nom"
            name="nom"
            onChange={(e)=>setNom(e.target.value)}
            required
            style={inputStyle}

            //   value={contact.nom}
            />
          </div>

          <div>
            <label style={labelStyle}>Email :</label>
            <input
            type="email"
            placeholder="Entrez l'email"
            name="mail"
            onChange={(e)=>setMail(e.target.value)}
            required
            style={inputStyle}

            //   value={contact.mail}
            />
          </div>

          <div>
            <label style={labelStyle}>Téléphone :</label>
            <PhoneInput
              country={'fr'}
              value={phone}
              onChange={handlePhoneChange}
              inputStyle={phoneInputStyle}
              buttonStyle={{
                backgroundColor: 'transparent',
                border: 'none',
                paddingLeft: '10px'
              }}
              dropdownStyle={{
                width: '300px'
              }}
              prefix="+"
              disableCountryCode={false}
              countryCodeEditable={false}
            />
          </div>
            {/* <link to="/"> */}
            <button type="submit" style={buttonStyle} onClick={handleClick} >Ajouter</button>
            {/* </link> */}
        </form>
      </div>
    </div>
        </>
    )
}
